&lt;div class='tableauPlaceholder' id='viz1748634956402' style='position: relative'&gt;

  &lt;!-- Static fallback image for no-JS browsers --&gt;
  &lt;noscript&gt;
    &lt;a href='#'&gt;
      &lt;img 
        alt='SV Cruises Executive Summary' 
        src='https://public.tableau.com/static/images/sv/sv_cruises_workbook2/Dashboard/1_rss.png' 
        style='border: none' 
      /&gt;
    &lt;/a&gt;
  &lt;/noscript&gt;

  &lt;!-- Tableau object (dashboard will appear here) --&gt;
  &lt;object class='tableauViz' style='display:none;'&gt;
    &lt;param name='host_url' value='https%3A%2F%2Fpublic.tableau.com%2F' /&gt; 
    &lt;param name='embed_code_version' value='3' /&gt; 
    &lt;param name='site_root' value='' /&gt;
    &lt;param name='name' value='sv_cruises_workbook2/Dashboard' /&gt;
    &lt;param name='tabs' value='no' /&gt;
    &lt;param name='toolbar' value='yes' /&gt;
    &lt;param name='static_image' value='https://public.tableau.com/static/images/sv/sv_cruises_workbook2/Dashboard/1.png' /&gt;
    &lt;param name='animate_transition' value='yes' /&gt;
    &lt;param name='display_static_image' value='yes' /&gt;
    &lt;param name='display_spinner' value='yes' /&gt;
    &lt;param name='display_overlay' value='yes' /&gt;
    &lt;param name='display_count' value='yes' /&gt;
    &lt;param name='language' value='en-US' /&gt;
  &lt;/object&gt;
&lt;/div&gt;

&lt;!-- Tableau loader script: dynamically resizes and loads dashboard --&gt;
&lt;script type='text/javascript'&gt;
  var divElement = document.getElementById('viz1748634956402');
  var vizElement = divElement.getElementsByTagName('object')[0];

  if (divElement.offsetWidth &gt; 800) {
    vizElement.style.width = '1300px';
    vizElement.style.height = '1196px';
  } else if (divElement.offsetWidth &gt; 500) {
    vizElement.style.width = '1300px';
    vizElement.style.height = '1196px';
  } else {
    vizElement.style.width = '100%';
    vizElement.style.height = '2227px';
  }

  var scriptElement = document.createElement('script');
  scriptElement.src = 'https://public.tableau.com/javascripts/api/viz_v1.js';
  vizElement.parentNode.insertBefore(scriptElement, vizElement);
&lt;/script&gt;
